import React, { Component } from 'react';
import './SliderShow.css';
import Slider from 'react-slick';
class SliderShow extends Component {
    constructor(props) {
        super(props);
        this.next = this.next.bind(this);
        this.previous = this.previous.bind(this);
    }
    next() {
        this.slider.slickNext();
    }
    previous() {
        this.slider.slickPrev();
    }
    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        return (
            <div>
                <h2>
                <img src ="https://www.insidevietnamtravel.com/images/icon-top10-chl.svg" alt = "img slider"/>
                </h2>                
                <h2>lastest halong bay cruise deals</h2>
                <p>We would like to bring you the newest deals. Save time & money - Travel more!</p>
                <Slider ref={c => (this.slider = c)} {...settings}>
                    <div key={1}>
                        <div className = "slider1">
                            <div className  = "column">
                            <img src = "https://d1k2oi80tv211b.cloudfront.net/uploads/photo/president-cruise-362-820-440.jpg" alt = "slider 1"/>
                            </div>
                        </div>
                    </div>
                    <div key={2}>
                        <h3>2</h3>
                    </div>
                    <div key={3}>
                        <h3>3</h3>
                    </div>
                    <div key={4}>
                        <h3>4</h3>
                    </div>
                    <div key={5}>
                        <h3>5</h3>
                    </div>
                    <div key={6}>
                        <h3>6</h3>
                    </div>
                </Slider>
                <div style={{ textAlign: "center" }}>
                    <button className="button" onClick={this.previous} />

                    <button className="button" onClick={this.next} />
                </div>
            </div>
        );
    }
}

export default SliderShow;
